    public void TooltipRenderEvent(Syncfusion.Blazor.Charts.TooltipRenderEventArgs Args)
    {
        var text = Args.Text.Split(':');
        var pointX = text[0];
        var y = float.Parse(text[1]);
        var pointY = y.ToString("#,##0");
        Args.Text = pointX + " : $" + pointY;
    }


